import React, { useRef } from 'react';
import { DateUtils } from "react-day-picker";
import DayPickerInput from 'react-day-picker/DayPickerInput';
import 'react-day-picker/lib/style.css';
import { Col, Row, Container } from "../components/Grid";

export default class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.handleDayChange = this.handleDayChange.bind(this);
    this.state = {
      selectedDay: undefined,
    };
  }

  handleDayChange(day) {
    this.setState({ selectedDay: day });
  }

  handleSubmit(event) {
    event.preventDefault();
  }

  render() {
    const mountainRef = useRef();

    const { selectedDay } = this.state;

    const test = DateUtils.isFutureDay(selectedDay);
    console.log(test);
    return (

      <Container fluid>
        <Row>
          <Col size="md-10" className="rounded border border-secondary">
            <h2>Signup</h2>

            <form className="form signup-form">
              <div className="form-group">
                {selectedDay && <p>Day: {selectedDay.toLocaleDateString()}</p>}
                {!selectedDay && <p>Choose a day</p>}
                <label htmlFor="day-trip">First Name:</label>
                <DayPickerInput id="day-trip" onDayChange={this.handleDayChange} />
              </div>
              <div className="form-group">
                <label htmlFor="mountains-trip">What Mountain?:</label>
                <select className="form-control" id="mountains-trip" name="mountains" ref={mountainRef} >
                  <option value="A-Basin">A-Basin</option>
                  <option value="Aspen">Aspen</option>
                  <option value="Breckenridge">Breckenridge</option>
                  <option value="Copper Mountain">Copper Mountain</option>
                  <option value="Crested Butte">Crested Butte</option>
                  <option value="Eldora">Eldora</option>
                  <option value="Keystone">Keystone</option>
                  <option value="Loveland">Loveland</option>
                  <option value="Steamboat">Steamboat</option>
                  <option value="Telluride">Telluride</option>
                  <option value="Vail">Vail</option>
                </select>
              </div>

              <div className="form-group">
                <button className="btn btn-secondary" type="submit" onClick={event => handleSubmit(event)} >Signup</button>
              </div>
            </form>
          </Col>
        </Row>
      </Container>
    );
  }
}
