import React, { useEffect, useState, useContext } from "react";
import { Link, useParams } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import MountainCard from "../components/MountainCard";
import API from "../utils/API";


function Mountain(props) {
  let hourlyWeather = [];
  let weatherArray = [];
  let city = props.city
  useEffect(() => {
    loadWeather()
  }, []);

  function loadWeather() {

    fetch("https://api.openweathermap.org/data/2.5/onecall?lat=" + props.lat + "&lon=" + props.lon + "&exclude=minutely&appid=9057e02af26cff387f193be4d1eee3ae")
      .then(response => response.json())
      .then(data => {
        console.log(data);
        let timestamp = data.daily[0].dt;
        let date = new Date(timestamp * 1000);
        console.log(date);
        //console.log(date.getDate() + (date.getMonth() + 1) + date.getFullYear());
        hourlyWeather = data.hourly;
        console.log(hourlyWeather)
      })
  }

  return (
      <Container fluid>
        <Row>
          <h1>
            7-Day Forecast
          </h1>
        </Row>
        <Row>
          <Col size="md-10 md-offset-1">
            <MountainCard
              city={city}
              hourlyWeather={hourlyWeather}
              weatherArray={weatherArray}
            />
          </Col>
        </Row>
        <Row>
          <Col size="md-2">
            <Link to="/" onClick={props.resetCity}>‚Üê Back to Home</Link>
          </Col>
        </Row>
      </Container>
    );
  }


export default Mountain;
