import React, { useEffect, useState, useContext } from "react";
import { Link, useParams } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import API from "../utils/API";


function Mountain(props) {


  useEffect(() => {
    loadWeather()
  }, []);

  function loadWeather() {
    let hourlyWeather = [];
    let weatherArray = [];
    fetch("https://api.openweathermap.org/data/2.5/onecall?lat=" + props.lat + "&lon=" + props.lon + "&exclude=minutely&appid=9057e02af26cff387f193be4d1eee3ae")
      .then(response => response.json())
      .then(data => hourlyWeather = data.hourly)
  }

  return (
      <Container fluid>
        <Row>
          <h1>
            7-Day Forecast
          </h1>
        </Row>
        <Row>
          <Col size="md-10 md-offset-1">

          </Col>
        </Row>
        <Row>
          <Col size="md-2">
            <Link to="/" onClick={props.resetCity}>‚Üê Back to Home</Link>
          </Col>
        </Row>
      </Container>
    );
  }


export default Mountain;
